public class WeekDays {
	public static String whichDay(int year, int month, int days) {
		double sum = 0;
		int difference = year-1754-1; //antal år från 1754 till det inskrivna året-1

		sum = difference*365; //antal dagar på dessa år utan skottdagar

		difference = difference-2; //antal år sedan första skottåret 1756

		double leap = (difference/4);
		double rest = difference % 4;

		double numberLeap = leap - rest;

		sum = sum + numberLeap;

		int numberDays;

		boolean checkYear = LeapYear.leapYear(year);
		if(checkYear == false) {
			numberDays = CalculateDays.calculate(year, month, days);

		}
		else {
			numberDays = CalculateDays.calculateLeap(year, month, days);
		}

		sum = sum + numberDays;

		if(sum % 7 == 0){
			return "måndag";
		}
		else if(sum % 7 == 1){
			return "tisdag";
		}
		else if(sum % 7 == 2){
			return "onsdag";
		}
		else if(sum % 7 == 3){
			return "torsdag";
		}
		else if(sum % 7 == 4){
			return "fredag";
		}
		else if(sum % 7 == 5){
			return "lördag";
		}
		else if(sum % 7 == 6){
			return "söndag";
		}
		else return "Error";

	}
}

