			if(year < 1754) {
				JOptionPane.showMessageDialog(null, "Ge ett årtal efter 1754. Skottår fanns inte före 1754 i Sverige.");
				continue;
			}

			JOptionPane.showMessageDialog(null, isLeapYear(year) ? "Skottår" : "Inte skottår");
