			String dayS = input.substring(i + 1);

			int year, month, day;
			try {
				year = Integer.parseInt(yearS);
				month = Integer.parseInt(monthS);
				day = Integer.parseInt(dayS);
			} catch(NumberFormatException e) {
				invalid();
				continue;
			}

			if(year < 1754) {
				JOptionPane.showMessageDialog(null, "Ge ett årtal efter 1754. Kalendern var annorlunda före 1754 i Sverige.");
				continue;
			}

			int dayN = getDayNumber(year, month, day);
			if(dayN == -1) {
				JOptionPane.showMessageDialog(null, "Datumet existerar inte.");
				continue;
			}
			JOptionPane.showMessageDialog(null, "Dagens nummer är " + dayN);
		}
	}

	public static void invalid() {
		JOptionPane.showMessageDialog(null, "Kunde inte tolka indatan som ett datum.");
	}

	public static final int[] monthDayCount = new int[] {31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31};

	public static int getDayNumber(int year, int month, int day) {
		if(month <= 0 || month > monthDayCount.length)
			return -1;
		int days = monthDayCount[month - 1];
		if(Steg1.isLeapYear(year) && month == 2)
			days++;
		if(day <= 0 || day > days)
			return -1;

		int cDays = 0;

		for(int i = 1; i < month; i++)
			cDays += monthDayCount[i - 1];
		if(month > 2 && Steg1.isLeapYear(year))
			cDays++;
		return cDays + day;
	}
}

